Collectible.objects.all().delete()

def main():
    for k, v in content.items():
        series = k
        names = v['names']
        images = v['images']
        for name, image in zip(names, images):
            name = ' '.join(word.capitalize() for word in name.lower().split())
            Collectible.objects.create(product='smiski', series=series, name=name, image=image)

main()